INSERT INTO `mnt_alm` (`id`, `name`, `repeaterDefault`, `repeaterType`, `pluginVersion`) VALUES
(1, 'default', '<?	 $color = get_field( \'bg_color\'); \n	 $anons_img = get_field( \'anons_img\'); \n	 $subtitle = get_field( \'subtitle\'); \n	 $anons = get_field( \'anons\'); \n	\n	//echo \'<pre>\'; print_r( $anons_img); echo \'</pre>\';\n	\n	$h = $anons_img[\'height\'] / ($anons_img[\'width\'] / 360);\n	\n	$scale = min(360 / $anons_img[\'width\'], 500 / $anons_img[\'height\'] );\n\n	if ($scale >= 1) {\n		$height = $anons_img[\'height\'];\n	}else{\n		$height = $anons_img[\'height\'] * $scale;\n	}\n		\n\n?>\n\n	<div class="post-wrap grid-item" style="background-color:<?=$color?>">\n	<a class="post-link " href="<?=the_permalink();?>">\n    \n     	\n            <div class="anons-img-wrap" style=" height:<?=$height?>px"><div class="anons-img anim" style="background-image:url(<?=$anons_img[\'url\'];?>)"></div></div>\n            <div class="anons-text">\n    	 	<h2><?php the_title(); ?></h2>\n    	 	<h3><?=$subtitle?></h3>\n    	 	<p><?=$anons?></p>\n            </div>\n             \n     </a>\n	</div>', 'default', '3.6.1');
INSERT INTO `mnt_comments` (`comment_ID`, `comment_post_ID`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_IP`, `comment_date`, `comment_date_gmt`, `comment_content`, `comment_karma`, `comment_approved`, `comment_agent`, `comment_type`, `comment_parent`, `user_id`) VALUES
(1, 1, 'Автор комментария', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2018-08-24 13:06:22', '2018-08-24 10:06:22', 'Привет! Это комментарий.\nЧтобы начать модерировать, редактировать и удалять комментарии, перейдите на экран «Комментарии» в консоли.\nАватары авторов комментариев загружаются с сервиса <a href="https://ru.gravatar.com">Gravatar</a>.', 0, 'post-trashed', '', '', 0, 0);
